import React, { useState } from "react";
import productseparation from "../images/produceseparation.png";
import vegetablesflying from "../images/vegetablesflying.jpeg";
function Main(props) {
  const [product, setProduct] = useState({
    name: "",
    type: "",
    expiryDate: "",
  });

  function onSubmit() {
    fetch("http://localhost:3000/fridge", {
      method: "POST", // or 'PUT'
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(product),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Success:", data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }

  return (
    <>
      <img src={vegetablesflying} />
      <h1>Insert product here</h1>
      <input
        name="product"
        type="text"
        onChange={(e) => {
          setProduct({ ...product, name: e.target.value });
        }}
      />
      <h1>Product type</h1>
      <input
        type="text"
        onChange={(e) => {
          setProduct({ ...product, type: e.target.value });
        }}
      />
      <h1>Use by date</h1>
      <input
        type="date"
        onChange={(e) => {
          setProduct({ ...product, expiryDate: e.target.value });
        }}
      />

      <button onClick={onSubmit}>Add Product</button>
      <button>Remove Product</button>
      <img src={productseparation} />
    </>
  );
}

export default Main;
